(function(){const header=document.querySelector('.site-header');if(!header)return;const onScroll=()=>{if(window.scrollY>8)header.classList.add('scrolled');else header.classList.remove('scrolled')};window.addEventListener('scroll',onScroll,{passive:true});onScroll()})();(function(){const items=document.querySelectorAll('.reveal');if(!items.length)return;if('IntersectionObserver' in window){const io=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting){entry.target.classList.add('show');io.unobserve(entry.target)}})},{threshold:.12});items.forEach(el=>io.observe(el))}else{items.forEach(el=>el.classList.add('show'))}})();
